mapper.update:
UPDATE user
SET passwd = #{passwd}, updated_at = #{updatedAt}
WHERE email = #{email}

UPDATE user
SET nickname = #{nickname}, updated_at = #{updatedAt}
WHERE id = #{id}

UPDATE user
SET role = #{role}, updated_at = #{updatedAt}
WHERE id = #{id}

UPDATE user
SET accessed_at = #{accessedAt}
WHERE id = #{id}
